$(document).ready(function() {
const pathname = window.location.pathname;
const search = window.location.search;
const page = $('.PageHeader');
const filter__hide = $('.filter__back');
  console.log(search)

if(search.includes('mlt_category')){
  page.addClass('nonactive');
  filter__hide.removeClass('nonactive');
}

$("#filter__back").attr("href", pathname); // Set the href attribute of the link
});